#include <stdio.h>
void swap(int *, int *); //prototype of the function
int main()
{
int a = 10;
int b = 20;
printf("Before swapping the values in main a = %d, b = %d\n",a,b);
swap(&a,&b);
printf("After swapping values in main a = %d, b = %d\n",a,b);
}
void swap (int *a, int *b)
{
int temp;
temp = *a;
*a=*b;
*b=temp;
printf("After swapping values in function a = %d, b = %d\n",*a,*b);
}

#include <stdio.h>
#include<math.h>

int isPrime(int x) {
int flag = 1;
for (int j = 2; j <= sqrt(x); ++j) {
if (x % j == 0) {
flag = 0;
break;
}
}
return(flag);
}

int main()
{
int a, b, i, j, flag;
printf("Enter lower bound of the interval: ");
scanf("%d", &a); // Take input
printf("Enter upper bound of the interval: ");
scanf("%d", &b); // Take input
printf("\nPrime numbers between %d and %d are: ", a, b);
for (i = a; i <= b; i++) {
if (i == 1 || i == 0)
continue;
if(isPrime(i))
printf("%d ", i);
}
return 0;
}
Instead of sqrt(x), x/2 or pow(x, 0.5) are alternate choices.
